import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TableColumn } from './interfaces/column.interface';
import { TableConfig } from './interfaces/config.interface';
import { TableAction } from './interfaces/action.interface';
import { TableSortService } from './services/table-sort.service';
import { TableFilterService } from './services/table-filter.service';

@Component({
  selector: 'app-generic-table',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './generic-table.component.html',
})
export class GenericTableComponent<T extends object> {
  @Input() data: T[] = [];
  @Input() config!: TableConfig<T>;
  @Output() rowClick = new EventEmitter<T>();

  searchText: string = '';
  sortColumn: string = '';
  sortDirection: 'asc' | 'desc' = 'asc';

  constructor(
    private sortService: TableSortService,
    private filterService: TableFilterService
  ) {}

  get filteredData(): T[] {
    let filtered = this.filterService.filter(
      this.data,
      this.searchText,
      this.config.columns
    );

    if (this.sortColumn) {
      const column = this.config.columns.find(
        (col) => col.key === this.sortColumn
      );
      if (column) {
        filtered = this.sortService.sort(filtered, column, this.sortDirection);
      }
    }

    return filtered;
  }

  sort(column: TableColumn<T>): void {
    if (!column.sortable) return;

    if (this.sortColumn === column.key) {
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column.key as keyof T as string;
      this.sortDirection = 'asc';
    }
  }

  getCellValue(item: T, column: TableColumn<T>): string | number {
    return column.render
      ? column.render(item)
      : (item[column.key as keyof T] as string | number);
  }

  handleAction(action: TableAction, item: T): void {
    action.onClick(item);
  }
}
<div class="w-full">
  <!-- Search Bar -->
  <div class="mb-4" *ngIf="config.showSearch">
    <input
      type="text"
      [(ngModel)]="searchText"
      placeholder="Search..."
      class="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
    />
  </div>

  <!-- Table -->
  <div class="overflow-x-auto rounded-lg border border-gray-200">
    <table
      class="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400"
    >
      <!-- Header -->
      <thead class="text-xs text-gray-950 border-b">
        <tr>
          <th
            *ngFor="let column of config.columns"
            [ngClass]="{
              'cursor-pointer hover:bg-gray-100': column.sortable
            }"
            (click)="sort(column)"
            class="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            <div class="flex items-center space-x-1">
              <span>{{ column.title }}</span>
              <span *ngIf="column.sortable" class="text-gray-400">
                <svg
                  *ngIf="sortColumn !== column.key"
                  class="w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M7 16V4m0 0L3 8m4-4l4 4m6 0v12m0 0l4-4m-4 4l-4-4"
                  />
                </svg>
                <svg
                  *ngIf="sortColumn === column.key && sortDirection === 'asc'"
                  class="w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M5 15l7-7 7 7"
                  />
                </svg>
                <svg
                  *ngIf="sortColumn === column.key && sortDirection === 'desc'"
                  class="w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M19 9l-7 7-7-7"
                  />
                </svg>
              </span>
            </div>
          </th>
          <th
            *ngIf="config.actions"
            class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Actions
          </th>
        </tr>
      </thead>

      <!-- Body -->
      <tbody>
        <tr
          *ngFor="let item of filteredData"
          (click)="rowClick.emit(item)"
          class="bg-white border-b dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600 cursor-pointer"
        >
          <td
            *ngFor="let column of config.columns"
            class="items-center px-4 py-4 text-gray-900 whitespace-nowrap dark:text-white"
          >
            <ng-container *ngIf="column.template; else defaultCell">
              <ng-container
                *ngTemplateOutlet="
                  column.template;
                  context: { $implicit: item, key: column.key }
                "
              ></ng-container>
            </ng-container>
            <ng-template #defaultCell>
              {{ getCellValue(item, column) }}
            </ng-template>
          </td>
          <td
            *ngIf="config.actions"
            class="px-6 py-4 whitespace-nowrap text-sm text-gray-900"
          >
            <div class="flex space-x-2">
              <button
                *ngFor="let action of config.actions"
                (click)="$event.stopPropagation(); handleAction(action, item)"
                [class]="
                  action.buttonClass ||
                  'px-3 py-1 rounded-md text-sm font-medium text-white bg-blue-600 hover:bg-blue-700'
                "
              >
                <span *ngIf="action.icon" class="mr-1">
                  <i [class]="action.icon"></i>
                </span>
                {{ action.label }}
              </button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
